import {
    BUTTON_CLICK,
    LEVEL_REPEAT
} from "./types";

export const changeFlagButtonClick = (arrLevelCompleted, letter, levelBlock, buttonBlocks, numberButton) => {
    //клик по кнопке со слогом помечает как true
    let arr = { ...arrLevelCompleted };
    arr[letter][levelBlock][buttonBlocks][numberButton] = true;

    return {
        type: BUTTON_CLICK,
        payload: arr
    }
}
export const nullAllFlagsLevelButtonClick = (arrLevelCompleted, letter, levelBlock) => {
    //клик по кнопке со слогом помечает как true
    let arr = { ...arrLevelCompleted };
    arr[letter][levelBlock] = [
                                    {
                                        'button_1': false,
                                        'button_2': false,
                                        'button_3': false,
                                    },
                                    {
                                        'button_1': false,
                                        'button_2': false,
                                        'button_3': false,
                                    },
                                    {
                                        'button_1': false,
                                        'button_2': false,
                                        'button_3': false,
                                    },
                                    {
                                        'button_1': false,
                                        'button_2': false,
                                        'button_3': false,
                                    },
                                    {
                                        'button_1': false,
                                        'button_2': false,
                                        'button_3': false,
                                    },
                                    {
                                        'button_1': false,
                                        'button_2': false,
                                        'button_3': false,
                                    },
                                ];

    return {
        type: LEVEL_REPEAT,
        payload: arr
    }
}
