import React, { useState} from 'react';
import { connect, useSelector } from "react-redux";

import Button from '../../UserInterface/Buttons/Button';


function LevelBlockWord(props) {

    const letterSelect = useSelector(state => state.stateLetters.letterSelect);
    const stateWords = useSelector(state => state.stateWords);

    const [counter, setCounter] = useState(0);

    const hendlerClickWord = (numberWord) => {
        setCounter(numberWord);
    }

    return (
        <>
            {
                counter === 11 ?
                    <div>Молоден! продожим дальше?</div>
                    :<div>
                        <div>{counter + 1}</div>
                        <Button
                            name={stateWords[letterSelect][props.numberWordBlock][counter]}
                            onclick={hendlerClickWord}
                            data={counter + 1}
                        />
                    </div>
            }

        
         </>
     )
}

export default connect()(LevelBlockWord)
