import React, {useState } from 'react';

import ButtonAutomationBlock from '../../UserInterface/Buttons/ButtonAutomationBlock';

function ButtonBlock(props) {

    const [flagClick, setFlagClick] = useState({
        'button1': false,
        'button2': false,
        'button3': false,
    })
    const classnameSpan = {
        'span1': ['heart'],
        'span2': ['heart'],
        'span3': ['heart'],
    }
    return (
        <div className="button-block">

            <ButtonAutomationBlock
                    classnameLetters={props.classnameLetters}
                    onclick={props.onclick}
                    names={props.namesLettersButton_1}
                    number={flagClick.button1}
            />

            <span className={classnameSpan.span1.join(' ')}>	
                &#129178;
            </span>

            <ButtonAutomationBlock
                    classnameLetters={props.classnameLetters}
                    onclick={props.onclick}
                    names={props.namesLettersButton_2}
                    number={flagClick.button2}
            />

            <span className={classnameSpan.span2.join(' ')}>	
                &#129178;
            </span>

            <ButtonAutomationBlock
                    classnameLetters={props.classnameLetters}
                    onclick={props.onclick}
                    names={props.namesLettersButton_3}
                    number={flagClick.button3}
            />

            <span className={classnameSpan.span3.join(' ')}>
                &#10084;
            </span>

        </div>

    )
}

export default ButtonBlock
