import React, { useState } from 'react';
import { connect, useDispatch, useSelector } from "react-redux";

import NavigationConsole from '../../components/Header/NavigationConsole';
import WindowBlind from '../../UserInterface/WindowBlind';
import ButtonScrollDownUp from '../../UserInterface/Buttons/ButtonScrollDownUp';


function Header(props) {

    const stateNavigationConsole = [
        {
            to: '/',
            name: 'Home'
        },
        {
            to: '/products',
            name: 'Products'
         },
         {
             to: '/productsAbout',
             name: 'ProductsAbout'
         }
    ]

    const [isOpen, setIsOpen] = useState(false);

    const navStyle = ['Navigation'];

    if (!isOpen) {
        navStyle.push('close');
    };

    const hendlerNavHide = () => {
        setIsOpen(false);
    }
    const hendlerNavShow = () => {
        setIsOpen(true);
    }

    let buttonScrollStyle = ['buttonScroll', 'goDown'];

    const [scrollDownUp, setScrollDownUp] = useState(true);

    if (!scrollDownUp) {
        buttonScrollStyle = ['buttonScroll', 'goUp'];
    }

    const hendlerScrollDownUp = () => {
        setScrollDownUp(!scrollDownUp);
    }


    return (
        <header>
            <nav
                onMouseOver={hendlerNavShow}
                onMouseOut={hendlerNavHide}
                onClick={hendlerNavHide}
                className={navStyle.join(' ')}
            >
                <NavigationConsole
                    stateNavigationConsole={stateNavigationConsole}
                />
               
                
            </nav>

            {
                isOpen ?
                    <WindowBlind
                        onclick={hendlerNavHide}
                        classname='NavigationWindowBlind'
                    />
                    : null
            }
            {
                isOpen ?
                    null
                    : <ButtonScrollDownUp
                        classname={buttonScrollStyle.join(' ')}
                        onclick={hendlerScrollDownUp}
                        nameButton="&#128640;"
                    />
            }

        </header>
    )
}
export default connect()(Header)


