import React, {useState } from 'react';

import ButtonAutomationBlock from '../../UserInterface/Buttons/ButtonAutomationBlock';

function ButtonBlock(props) {

    const [classnameSpan, setClassnameSpan] = useState({
        'button1': ['heart'],
        'button2': ['heart'],
        'button3': ['heart'],
    })

    const hendlerButtonClick = (data) => {
        console.log('click ', data);
        let newClass = {
            ...classnameSpan,
            [data]: ['heart', 'clicked'],
        }
        setClassnameSpan(newClass);

    }

    return (
        <div className="button-block">

            <ButtonAutomationBlock
                    classnameLetters={props.classnameLetters}
                    onclick={hendlerButtonClick}
                    names={props.namesLettersButton_1}
                    number='button1'
            />

            <span
                className={classnameSpan.button1.join(' ')}
            >	
                &#129178;
            </span>

            <ButtonAutomationBlock
                    classnameLetters={props.classnameLetters}
                    onclick={hendlerButtonClick}
                    names={props.namesLettersButton_2}
                    number='button2'
            />

            <span
                className={classnameSpan.button2.join(' ')}
            >	
                &#129178;
            </span>

            <ButtonAutomationBlock
                    classnameLetters={props.classnameLetters}
                    onclick={hendlerButtonClick}
                    names={props.namesLettersButton_3}
                    number='button3'
            />

            <span
                className={classnameSpan.button3.join(' ')}
            >
                &#10084;
            </span>

        </div>

    )
}

export default ButtonBlock
