import React, { useState } from 'react';
import { connect, useDispatch, useSelector } from "react-redux";

import ButtonBlock from './ButtonBlock';

function LevelBlock(props) {


    const coin = useSelector(state => state.stateLevelCoins.letterBlocks[props.numberLevelBlock]);
    const stateLetters = useSelector(state => state.stateLettersBlocks[props.numberLevelBlock]);

    //[
    //    {
    //        'namesLettersButton_1': { 'letter_1': 'X', 'letter_2': 'А', 'letter_3': '' },
    //        'namesLettersButton_2': { 'letter_1': 'X', 'letter_2': 'А', 'letter_3': '' },
    //        'namesLettersButton_3': { 'letter_1': 'X', 'letter_2': 'А', 'letter_3': '' },
    //    },
    //    {
    //        'namesLettersButton_1': { 'letter_1': 'X', 'letter_2': 'О', 'letter_3': '' },
    //        'namesLettersButton_2': { 'letter_1': 'X', 'letter_2': 'О', 'letter_3': '' },
    //        'namesLettersButton_3': { 'letter_1': 'X', 'letter_2': 'О', 'letter_3': '' },
    //    },
    //    {
    //        'namesLettersButton_1': { 'letter_1': 'X', 'letter_2': 'У', 'letter_3': '' },
    //        'namesLettersButton_2': { 'letter_1': 'X', 'letter_2': 'У', 'letter_3': '' },
    //        'namesLettersButton_3': { 'letter_1': 'X', 'letter_2': 'У', 'letter_3': '' },
    //    },
    //    {
    //        'namesLettersButton_1': { 'letter_1': 'X', 'letter_2': 'И', 'letter_3': '' },
    //        'namesLettersButton_2': { 'letter_1': 'X', 'letter_2': 'И', 'letter_3': '' },
    //        'namesLettersButton_3': { 'letter_1': 'X', 'letter_2': 'И', 'letter_3': '' },
    //    },
    //    {
    //        'namesLettersButton_1': { 'letter_1': 'X', 'letter_2': 'Е', 'letter_3': '' },
    //        'namesLettersButton_2': { 'letter_1': 'X', 'letter_2': 'Е', 'letter_3': '' },
    //        'namesLettersButton_3': { 'letter_1': 'X', 'letter_2': 'Е', 'letter_3': '' },
    //    },
    //    {
    //        'namesLettersButton_1': { 'letter_1': 'X', 'letter_2': 'Ы', 'letter_3': '' },
    //        'namesLettersButton_2': { 'letter_1': 'X', 'letter_2': 'Ы', 'letter_3': '' },
    //        'namesLettersButton_3': { 'letter_1': 'X', 'letter_2': 'Ы', 'letter_3': '' },
    //    },
    //    {
    //        'classnameButton': 'buttonLetter',
    //        'classnameLetter_1': 'letter_x',
    //        'classnameLetter_2': 'letter_1',
    //        'classnameLetter_3': 'letter_1'
    //    }
    //]

    // меняем переменную 'X' в state на переданную через props.letterAutomation букву
    for (let i = 0; i < stateLetters.length; i++) {

        let arr = Object.values(stateLetters[i])
        //[       { 'letter_1': 'X', 'letter_2': 'Ы', 'letter_3': '' },
        //        { 'letter_1': 'X', 'letter_2': 'Ы', 'letter_3': '' },
        //        { 'letter_1': 'X', 'letter_2': 'Ы', 'letter_3': '' },]

        console.log(arr)

        for (let obj of arr) {
            //console.log(obj)
            //if (obj['letter_1'] === 'X') {
            //    obj['letter_1'] = props.letterAutomation;
            //}
            //if (obj['letter_2'] === 'X') {
            //    obj['letter_2'] = props.letterAutomation;
            //}

            let letters = Object.values(obj)
            console.log(letters)
            for (let letter of letters) {
                if (letter === 'X') {
                    letter = props.letterAutomation;
                }
            }

        }       
    };

    const [levelСompleted, setLevelCompleted] = useState([false, false, false, false, false, false])

    const hendlerButtonBlock = (data, numberBlock) => {
        console.log('back ', data) 
        if (data === true) {
            let flagArr = [...levelСompleted];
            flagArr[numberBlock] = true;
            setLevelCompleted(flagArr);
            
            if (flagArr.every(x => x === true) === true) {
                console.log('level completed ', true);

            }
        }
    }

    return (

            <div>
                <ButtonBlock
                    namesLettersButtons={stateLetters[0]}
                    classname={stateLetters[6]}
                    hendlerButtonBlock={hendlerButtonBlock}
                    numberBlock={0}
                    coin={coin}
                />
                <ButtonBlock
                    namesLettersButtons={stateLetters[1]}
                    classname={stateLetters[6]}
                    hendlerButtonBlock={hendlerButtonBlock}
                    numberBlock={1}
                    coin={coin}
                />
                <ButtonBlock
                    namesLettersButtons={stateLetters[2]}
                    classname={stateLetters[6]}
                    hendlerButtonBlock={hendlerButtonBlock}
                    numberBlock={2}
                    coin={coin}
                />
                <ButtonBlock
                    namesLettersButtons={stateLetters[3]}
                    classname={stateLetters[6]}
                    hendlerButtonBlock={hendlerButtonBlock}
                    numberBlock={3}
                    coin={coin}
                />
                <ButtonBlock
                    namesLettersButtons={stateLetters[4]}
                    classname={stateLetters[6]}
                    hendlerButtonBlock={hendlerButtonBlock}
                    numberBlock={4}
                    coin={coin}
                />
                <ButtonBlock
                    namesLettersButtons={stateLetters[5]}
                    classname={stateLetters[6]}
                    hendlerButtonBlock={hendlerButtonBlock}
                    numberBlock={5}
                    coin={coin}
                />

            </div>
        
     )
}

export default connect()(LevelBlock)
