import React, { useEffect, useState } from 'react';
import { connect, useDispatch, useSelector } from "react-redux";

import FlagsLevelsСompleted from '../../containers/Automation/FlagsLevelsСompleted';
import SelectingLevelBlock from '../../UserInterface/SelectingLevelBlock';
import LevelBlockWord from '../../components/WordsAutomation/LevelBlockWord';

import {
    sourceStateLettersBlocks,
} from '../../store/LettersBlocks/actionsLettersBlocks';
import {
    changeFlagLevelWordCompleted
} from '../../store/LevelCompleted/actionsLevelWordsFinal';

function WordBlocks(props) {

    const dispatch = useDispatch();

    const letterSelect = useSelector(state => state.stateLetters.letterSelect);
    const namesLevelBlock = useSelector(state => state.stateLevelCoins.letterBlocks);
    const stateLevelCompleted = useSelector(state => state.stateLevelCompleted);//флаги всех кнопок ButtonBlocks
    const stateLevelWordsFinal = useSelector(state => state.stateLevelWordsFinal); //флаги пройденных блоков в листе
    const stateButtonBlockFinal = useSelector(state => state.stateButtonBlockFinal);//флаги пройденных
    const stateFinalLetter = useSelector(state => state.stateFinalLetter); //BUTTON_LEVEL_COMPLETED
    const stateLevelEnd = useSelector(state => state.stateLevelEnd); //флаги не полученных наград за окончание уровня
    const stateWords = useSelector(state => state.stateWords);
    

    
    const [numberWordBlock, setNumberWordBlock] = useState(0);

    const hendlerSelectWordBlock = (data) => {
        setNumberWordBlock(data);
        console.log('hendlerSelectWordBlock ', data)
    }
    const coin = useSelector(state => state.stateLevelCoins.letterBlocks[numberWordBlock]);
    const stateLetters = useSelector(state => state.stateLettersBlocks[numberWordBlock]);


    //счетчик переключиния слов, в массиве 10 слов
    const [counter, setCounter] = useState(0);
    const hendlerClickWord = (numberWord) => {
        setCounter(numberWord);
        if (numberWord === 10) {
            dispatch(changeFlagLevelWordCompleted(stateLevelWordsFinal, letterSelect, numberWordBlock));
        }
    }
    const hendlerNextLevel = () => {

    }

    useEffect(() => {
        return () => {
            dispatch(sourceStateLettersBlocks());
        }
    }, [])

    return (
        <div className="automation-buttons-blocks">


            <div>
                Тип буквы {props.typeLetter} буква {letterSelect}

                <SelectingLevelBlock
                    namesLevelBlock={namesLevelBlock}
                    onclick={hendlerSelectWordBlock}
                    levelsComplied={stateLevelWordsFinal[letterSelect]}
                />
            </div>

            <LevelBlockWord
                onclickWord={hendlerClickWord}
                data={counter + 1}
                name={stateWords[letterSelect][numberWordBlock][counter]}
                flagLevelFinal={stateLevelWordsFinal[letterSelect][numberWordBlock]}
                coin={coin}
            />

            <FlagsLevelsСompleted />
               
        </div>
        
     )
}

export default connect()(WordBlocks)


