import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';
import { connect, useDispatch, useSelector } from "react-redux";

import { selectingLetter } from '../../store/LettersBlocks/actionsLetters';
import {
    changeLettersBlocks,
} from '../../store/LettersBlocks/actionsLettersBlocks';



function TypesLetters(props) {

    const dispatch = useDispatch();

    const stateLettersRedux = useSelector(state => state.stateLettersBlocks);
    const letters = useSelector(state => state.stateLetters.letters);

    const hendlerClick = (letterName) => {
        dispatch(selectingLetter(letterName));
        dispatch(changeLettersBlocks(stateLettersRedux, letterName));
    };

    //if (active == false) {
    //    button[i].classList.add('circular-menu__button_active');
    //    active = true; // сохраняем в переменной статус активности

    //    // перебираем все элементы, вычисляем координаты
    //    for (let j = 0; j < items.length; j++) {
    //        const angle = j * arc;
    //        const x = radius * Math.cos(angle);
    //        const y = radius * Math.sin(angle);

    //        //устанавливаем координаты каждому элементу
    //        items[j].style.left = 50 + x + '%';
    //        items[j].style.top = 50 + y + '%';
    //    };
    const [flagOpenMenu, setFlagOpenMenu] = useState(false);

    const hendlerOpenMenu = () => {
        setFlagOpenMenu(!flagOpenMenu);
    }

    return(
    <div className="circular">
        <div className="circular-menu">
            {
                    letters.map((item, index) => {

                        if (item.type === 2 && flagOpenMenu) {
                            return <div
                                key={index + "TypesLetters"}
                                onClick={() => hendlerClick(item.name)}
                                className='circular-menu__button_active'
                                style={{ left: '`${50 + radius * Math.cos(index * arc)}`%', right: '`${50 + radius * Math.sin(index * arc)}`%'}}
                                    >
                                        <NavLink to={`/automation/${item.type}/${item.name}/LevelBlock_1`}>{item.name}</NavLink>
                                    </div>
                        }
                    })
            }
                <div
                    class="circular-menu__button"
                    onClick={hendlerOpenMenu}
                >
                <span class="circular-menu__lines">O</span>
            </div>
        </div>
    </div>
    )
}


export default connect()(TypesLetters)
