import {
    LETTERS_SELECT
} from "./types";

const initialState = {
    'letterSelect': 'X',
};



const stateLetters = (state = initialState, action) => {

    switch (action.type) {

        case LETTERS_SELECT:
            return {
                ...state,
                letterSelect: action.payload
            };
        default:
            return state;
    }
}
export default stateLetters
