import React, { useState, useEffect } from 'react';


function ButtonScrollDownUp(props) {

    const [flagButtonScroll, setFlagButtonScroll] = useState(false);

    const haveButtonScroll = () => {
        if (document.documentElement.scrollHeight > document.documentElement.clientHeight) {
            setFlagButtonScroll(true);
        }
    }

   // const buttonScrollStyle = ['buttonScroll'];

    const [scrollDownUp, setScrollDownUp] = useState(true);

    const [buttonScrollStyle, setButtonScrollStyle] = useState(['buttonScroll'])

    if (scrollDownUp) {
       // buttonScrollStyle.push('goUp');
        setButtonScrollStyle(['buttonScroll', 'goUp'])
        window.scrollTo(0, document.documentElement.scrollHeight);
    } else {
       // buttonScrollStyle.push('goDown');
        setButtonScrollStyle(['buttonScroll', 'goDown'])
        window.scrollTo(0, 0);
    }

    const hendlerScrollDownUp = () => {
        setScrollDownUp(!scrollDownUp);
    }

    const scrollXY = () => {

        console.log(window.pageYOffset)

        let height = document.documentElement.clientHeight + 100; //758 + 100
        let scroll = document.documentElement.scrollHeight - window.pageYOffset;
        if (height > scroll) {
            console.log("!!!!!")
            setScrollDownUp(!scrollDownUp);
        }
    }

    const ButtonScroll = () => {
        return <div
                    className={buttonScrollStyle.join(' ')}
                    onClick={hendlerScrollDownUp}
                >
                    <div>
                        {props.nameButton}
                    </div>
                </div>
    }

    useEffect(() => {

        document.addEventListener('scroll', scrollXY);
        document.addEventListener('scroll', haveButtonScroll);
    }, [])

    return(

        flagButtonScroll ?
            ButtonScroll()
            : null
            
    )
}

export default ButtonScrollDownUp
