import React, { useState, useEffect }  from 'react';
import { Switch, Route } from 'react-router-dom';


import Home from '../Home/Home';
import Products from '../Products/Products';
import ProductsAbout from '../Products/ProductsAbout';
import NotFound from '../NotFound/NotFound';

import ButtonScrollDownUp from '../../UserInterface/Buttons/ButtonScrollDownUp';




export const Main = () => {

    //const [isHeightLarge, setIsHeightLarge] = useState(false);

    let buttonScrollStyle = ['buttonScroll', 'goDown'];

    const [scrollDownUp, setScrollDownUp] = useState(true);

    if (scrollDownUp) {
        buttonScrollStyle = ['buttonScroll', 'goUp'];
        window.scrollTo(0, document.documentElement.scrollHeight);
    } else {
        window.scrollTo(0, 0);
    }

    const hendlerScrollDownUp = () => {
        setScrollDownUp(!scrollDownUp);
    }

    //useEffect(() => {
    //    console.log('clientHeight ', document.documentElement.clientHeight);
    //    console.log('scrollHeight', document.documentElement.scrollHeight);
    //    if (document.documentElement.clientHeight < document.documentElement.scrollHeight) {
    //        let flag = true;
    //        setIsHeightLarge(flag);
    //        console.log('больше')
    //    };
    //}, []);

    return (
        <main>


            <ButtonScrollDownUp
                classname={buttonScrollStyle.join(' ')}
                onclick={hendlerScrollDownUp}
                nameButton="&#128640;"
            />
 
            <Switch>
                <Route exact path='/' component={Home} />




                <Route path='/products' component={Products} />

                <Route path='/productsAbout/:id?/:category?' component={ProductsAbout} />

                <Route component={NotFound} />
            </Switch>
        </main>
    )
}
