import React from 'react';




class ProductsAbout extends React.Component {

    render() {

        const prodId = this.props.match.params.id;
        const cat = this.props.match.params.category;

        return <div>
            <h2>Товар</h2>
            <p>Категория {cat}</p>
            <p>Идентификатор {prodId}</p>
        </div>;
    }
}

export default ProductsAbout

