import {
    LETTER_LEVEL_END,
    LOAD_FROM_LOCAL_STORAGE_LETTER_COINS
} from "./types";

export const flagLevelLetterEnd = (type, stateLevelEnd, letter) => {
    //все уровни пройдены, флаг true

    let obj = { ...stateLevelEnd };

    obj[type][letter] = true;

    return {
        type: LETTER_LEVEL_END,
        payload: obj
    }
}
export const loadLocalStorageLetterCoins = (data) => {
    //при входе в игру загружаем из localStorage 

    return {
        type: LOAD_FROM_LOCAL_STORAGE_LETTER_COINS,
        payload: data
    }
}
