                    classname={props.stateLetters[6]}
                    hendlerButtonBlock={hendlerButtonBlock}
                    numberBlock={0}
                    coin={props.coin}
                />
                <ButtonBlock
                    namesLettersButtons={props.stateLetters[1]}
                    classname={props.stateLetters[6]}
                    hendlerButtonBlock={hendlerButtonBlock}
                    numberBlock={1}
                    coin={props.coin}
                />
                <ButtonBlock
