import ButtonBlock from './ButtonBlock';

function LevelBlock(props) {


    const coin = useSelector(state => state.stateLevelCoins.letterBlocks[props.numberLevelBlock]);
    const stateLetters = useSelector(state => state.stateLettersBlocks[props.numberLevelBlock]);

    //[
    //    {
    //        'namesLettersButton_1': { letter_1: 'А', letter_2: 'X', letter_3: 'А' },
    //        'namesLettersButton_2': { letter_1: 'А', letter_2: 'X', letter_3: 'А' },
