    //],


    //for (let obj of stateLetters) {
        
    //     console.log(obj)    
    //    let objKeys = Object.keys(obj);
        
    //    for (let key in obj) {
    //        console.log(key) 
    //    }
    //};
    for (let i = 0; i < stateLetters.length; i++) {
        
        let objKeys = Object.keys(stateLetters[i]);

        let arr = Object.values(stateLetters[i])

        for (let obj of arr) {
            let arrObj = Object.values(obj)
            console.log(arrObj)
            for (let letter of arrObj) {
                if (letter === 'X') {
                    letter = 'Y'
                }
            }
        }

        

        //for (let j = 0; j < objKeys.length; j++) {
        //  //  console.log(stateLetters[i].objKeys[j])
        //    console.log(objKeys[j])
        //}

        
    }
   








    const [levelСompleted, setLevelCompleted] = useState([false, false, false, false, false, false])

    const hendlerButtonBlock = (data, numberBlock) => {
        console.log('back ', data) 
        if (data === true) {
            let flagArr = [...levelСompleted];
            flagArr[numberBlock] = true;
            setLevelCompleted(flagArr);
            
            if (flagArr.every(x => x === true) === true) {
                console.log('level completed ', true);

            }
        }
    }

    return (

            <div>
                <ButtonBlock
                    namesLettersButtons={stateLetters[0]}
                    classname={stateLetters[6]}
                    hendlerButtonBlock={hendlerButtonBlock}
                    numberBlock={0}
                    coin={coin}
                />
                <ButtonBlock
                    namesLettersButtons={stateLetters[1]}
                    classname={stateLetters[6]}
                    hendlerButtonBlock={hendlerButtonBlock}
                    numberBlock={1}
                    coin={coin}
                />
                <ButtonBlock
                    namesLettersButtons={stateLetters[2]}
                    classname={stateLetters[6]}
                    hendlerButtonBlock={hendlerButtonBlock}
                    numberBlock={2}
                    coin={coin}
                />
                <ButtonBlock
                    namesLettersButtons={stateLetters[3]}
                    classname={stateLetters[6]}
                    hendlerButtonBlock={hendlerButtonBlock}
                    numberBlock={3}
                    coin={coin}
                />
                <ButtonBlock
                    namesLettersButtons={stateLetters[4]}
                    classname={stateLetters[6]}
                    hendlerButtonBlock={hendlerButtonBlock}
                    numberBlock={4}
                    coin={coin}
                />
                <ButtonBlock
                    namesLettersButtons={stateLetters[5]}
                    classname={stateLetters[6]}
                    hendlerButtonBlock={hendlerButtonBlock}
                    numberBlock={5}
                    coin={coin}
                />

            </div>
        
     )
}

export default connect()(LevelBlock)
