import React, { useState } from 'react';
import { connect, useDispatch, useSelector } from "react-redux";

import NavigationConsole from '../../UserInterface/NavigationConsole';
import WindowBlind from '../../components/Header/WindowBlind';

function Header(props) {

    const stateNavigationConsole = [
        {
            to: '/',
            name: 'Home üè∞'
        },
        {
            to: '/prizeShop',
            name: '–í—ã–±–æ—Ä –Ω–∞–≥—Ä–∞–¥—ã üèÜ'
        },
        {
            to: '/progress',
            name: '–ü—Ä–æ–≥—Ä–µ—Å—Å'
        },
    ]

    const [isOpen, setIsOpen] = useState(false);
    const navStyle = ['Navigation'];

    if (!isOpen) {
        navStyle.push('close');
    };

    const hendlerNavHide = () => {
        setIsOpen(false);
    }
    const hendlerNavShow = () => {
        setIsOpen(true);
    }

    const namePlayer = useSelector(state => state.stateAuthorization.player);

    //–Ω–∞–±–æ—Ä –∫–∞—Ä—Ç–∏–Ω–æ–∫ –¥–ª—è –ø—Ä–∏–∑–æ–≤
    const stateImages = useSelector(state => state.stateImages);
    const letterProgressPrizes = useSelector(state => state.stateLevelEnd); //–ø—Ä–æ–≥—Ä–µ—Å—Å –±—É–∫–≤ –∏ –ø–æ–ª—É—á–µ–Ω–Ω—ã—Ö –ø—Ä–∏–∑–æ–≤

    //–ø–µ—Ä–µ–º–µ–Ω–Ω—ã–µ —Ñ–ª–∞–≥–æ–≤ –∫–∞—Ä—Ç–æ—á–µ–∫ —Å–ª–æ–≥–æ–≤
    const stateLevelCompleted = useSelector(state => state.stateLevelCompleted);//—Ñ–ª–∞–≥–∏ –≤—Å–µ—Ö –∫–Ω–æ–ø–æ–∫ ButtonBlocks
    const stateLevelFinal = useSelector(state => state.stateLevelFinal); //—Ñ–ª–∞–≥–∏ –ø—Ä–æ–π–¥–µ–Ω–Ω—ã—Ö –±–ª–æ–∫–æ–≤ –≤ —É—Ä–æ–≤–Ω–µ
    const stateButtonBlockFinal = useSelector(state => state.stateButtonBlockFinal);//—Ñ–ª–∞–≥–∏ —É—Ä–æ–≤–Ω–µ–π –≤ –±—É–∫–≤–µ
    const stateFinalLetter = useSelector(state => state.stateFinalLetter); //—Ñ–ª–∞–≥ –±—É–∫–≤–∞ –ø—Ä–æ–π–¥–µ–Ω–∞

    //–ø–µ—Ä–µ–º–µ–Ω–Ω—ã–µ —Ñ–ª–∞–≥–æ–≤ —É—Ä–æ–≤–Ω–µ–π —Å–ª–æ–≤
    const stateLevelWordsFinal = useSelector(state => state.stateLevelWordsFinal); //—Ñ–ª–∞–≥–∏ –ø—Ä–æ—Ö–æ–∂–¥–µ–Ω–∏—è —É—Ä–æ–≤–Ω–µ–π –≤ –±—É–∫–≤–µ
    const stateFinalLetterWords = useSelector(state => state.stateFinalLetterWords);// —Ñ–ª–∞–≥–∏ –ø—Ä–æ—Ö–æ–∂–¥–µ–Ω–∏—è –±—É–∫–≤—ã

  
    return (
        <header>
            <nav
                onMouseOver={hendlerNavShow}
                onMouseOut={hendlerNavHide}
                onClick={hendlerNavHide}
                className={navStyle.join(' ')}
            >
                <div>–ü–†–ò-–í–ï–¢, {namePlayer}</div>

                <NavigationConsole
                    stateNavigationConsole={stateNavigationConsole}
                    onclick={()=>console.log('click Header NavigationConsole')}
                />

                
            </nav>


            {
                isOpen ?
                    <WindowBlind
                        onclick={hendlerNavHide}
                        classname='NavigationWindowBlind'
                        letterProgressPrizes={letterProgressPrizes}
                        stateImages={stateImages}
                    />
                    : null
            }
           

        </header>
    )
}
export default connect()(Header)
