import React, { useState, useEffect } from 'react';


function ButtonScrollDownUp(props) {

    const buttonScrollStyle = ['buttonScroll'];

    const [scrollDownUp, setScrollDownUp] = useState(true);

    if (scrollDownUp) {
        buttonScrollStyle.push('goUp');
        window.scrollTo(0, document.documentElement.scrollHeight);
    } else {
        buttonScrollStyle.push('goDown');
        window.scrollTo(0, 0);
    }

    const hendlerScrollDownUp = () => {
        setScrollDownUp(!scrollDownUp);
    }

    const scrollXY = () => {

        console.log(document.documentElement.scrollHeight - window.pageYOffset)
        console.log('высота окна ', document.documentElement.clientHeight)
        if (document.documentElement.clientHeight > (document.documentElement.scrollHeight - window.pageYOffset)) {
            console.log("!!!!!")
        }
    }
    useEffect(() => {

        document.addEventListener('scroll', scrollXY);
    }, [])

    return(
           
            <div 
                className={buttonScrollStyle.join(' ')}
                onClick={hendlerScrollDownUp}
            >   
                <div>
                    {props.nameButton}
                </div>          
            </div>
    )
}

export default ButtonScrollDownUp
